    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
         String BookID=jTextField1.getText(); 
         String Bookname=jTextField2.getText(); 
         String publisher=jTextField3.getText(); 
         String price=jTextField4.getText(); 
         String publisheryear=jTextField5.getText(); 
         try{
         Connection con=connectionprovider.getcon();
          Statement st=con.createStatement();
          st.executeUpdate("insert into book values('"+BookID+"','"+Bookname+"','"+publisher+"','"+price+"','"+publisheryear+"')");
          JOptionPane.showMessageDialog(null,"Successfully add");
          setVisible(false);
          new newbook().setVisible(true);
         }
          catch (Exception e)
      {
          JOptionPane.showMessageDialog(null,"Book allready existed");
          setVisible(false);
          new newbook().setVisible(true);

      }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

